pragma solidity ^0.8.26;  

import "./interfaces/ITenderRegistry.sol";

contract TenderAward {
    ITenderRegistry public tenderRegistry;

    constructor(address registryAddress) {
        tenderRegistry = ITenderRegistry(registryAddress);
    }
}

contracts/TenderRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

interface ITenderRegistry {
    function createTender(string calldata title) external returns (uint256);
    function acceptBid(uint256 tenderId, address bidder, uint256 amount) external;
}

contract TenderRegistry is ITenderRegistry {
    uint256 public nextTenderId;
    
    struct Tender {
        string title;
        address[] bidders;
        mapping(address => uint256) bids;
        bool exists;
    }

    mapping(uint256 => Tender) private tenders;

    event TenderCreated(uint256 indexed tenderId, string title);
    event BidAccepted(uint256 indexed tenderId, address indexed bidder, uint256 amount);

    function createTender(string calldata title) external returns (uint256) {
        uint256 tenderId = nextTenderId++;
        Tender storage t = tenders[tenderId];
        t.title = title;
        t.exists = true;
        emit TenderCreated(tenderId, title);
        return tenderId;
    }

    function acceptBid(uint256 tenderId, address bidder, uint256 amount) external {
        require(tenders[tenderId].exists, "Tender does not exist");
        Tender storage t = tenders[tenderId];
        if (t.bids[bidder] == 0) {
            t.bidders.push(bidder);
        }
        t.bids[bidder] = amount;
        emit BidAccepted(tenderId, bidder, amount);
    }

    // helper for tests
    function getBid(uint256 tenderId, address bidder) external view returns (uint256) {
        return tenders[tenderId].bids[bidder];
    }

    function getBidders(uint256 tenderId) external view returns (address[] memory) {
        return tenders[tenderId].bidders;
    }
}

contracts/TenderScoringRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import "@openzeppelin/contracts/access/Ownable.sol";

contract TenderScoringRegistry is Ownable {

    // -------------------------
    // Constructor
    // -------------------------
    constructor() Ownable(msg.sender) {}  // ✅ Pass deployer to base

    /// -------------------------
    /// Roles
    /// -------------------------
    address public scoringOracle;

    modifier onlyOracle() {
        require(msg.sender == scoringOracle, "Not authorized oracle");
        _;
    }

    /// -------------------------
    /// Data Structures
    /// -------------------------
    struct Score {
        uint16 totalScore;      // 0–10000 (2 decimal precision)
        bytes32 reportHash;     // Hash of JSON/PDF report
        bool exists;
    }

    mapping(uint256 => mapping(address => Score)) public scores;

    /// -------------------------
    /// Events
    /// -------------------------
    event OracleUpdated(address indexed oracle);
    event ScoreSubmitted(
        uint256 indexed tenderId,
        address indexed bidder,
        uint16 totalScore,
        bytes32 reportHash
    );

    /// -------------------------
    /// Admin Functions
    /// -------------------------
    function setScoringOracle(address oracle) external onlyOwner {
        require(oracle != address(0), "Invalid oracle");
        scoringOracle = oracle;
        emit OracleUpdated(oracle);
    }

    /// -------------------------
    /// Oracle Functions
    /// -------------------------
    function submitScore(
        uint256 tenderId,
        address bidder,
        uint16 totalScore,
        bytes32 reportHash
    ) external onlyOracle {
        require(bidder != address(0), "Invalid bidder");
        require(totalScore <= 10_000, "Score out of range");
        require(reportHash != bytes32(0), "Invalid report hash");
        require(!scores[tenderId][bidder].exists, "Score already submitted");

        scores[tenderId][bidder] = Score({
            totalScore: totalScore,
            reportHash: reportHash,
            exists: true
        });

        emit ScoreSubmitted(tenderId, bidder, totalScore, reportHash);
    }

    /// -------------------------
    /// View Functions
    /// -------------------------
    function getScore(uint256 tenderId, address bidder)
        external
        view
        returns (uint16 totalScore, bytes32 reportHash)
    {
        Score memory s = scores[tenderId][bidder];
        require(s.exists, "Score not found");
        return (s.totalScore, s.reportHash);
    }
}

contracts/TreasuryEscrow.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.26;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./TenderAward.sol";

contract TreasuryEscrow is ReentrancyGuard, Ownable {

    constructor(
        address _tenderAwardAddress,
        address _paymentTokenAddress
    ) Ownable(msg.sender) {
        tenderAward = TenderAward(_tenderAwardAddress);
        paymentToken = IERC20(_paymentTokenAddress);
    }

    struct Milestone {
        string description;
        uint256 amount;
        bool completed;
        bool paid;
        uint256 completionTime;
    }

    struct EscrowContract {
        uint256 tenderId;
        address winner;
        uint256 totalAmount;
        uint256 amountReleased;
        bool isActive;
        Milestone[] milestones;
    }

    TenderAward public tenderAward;
    IERC20 public paymentToken;

    mapping(uint256 => EscrowContract) private escrows;
    mapping(uint256 => bool) public escrowExists;

    event FundsLocked(uint256 indexed tenderId, address indexed winner, uint256 amount);
    event MilestoneCompleted(uint256 indexed tenderId, uint256 milestoneIndex);
    event FundsReleased(uint256 indexed tenderId, address indexed recipient, uint256 amount);
    event EscrowClosed(uint256 indexed tenderId);

    function createEscrow(
        uint256 _tenderId,
        address _winner,
        uint256 _totalAmount,
        string[] calldata _milestoneDescriptions,
        uint256[] calldata _milestoneAmounts
    ) external onlyOwner {
        require(!escrowExists[_tenderId], "Escrow already exists");
        require(_milestoneDescriptions.length == _milestoneAmounts.length, "Array length mismatch");

        uint256 totalMilestoneAmount;

        EscrowContract storage escrow = escrows[_tenderId];
        escrow.tenderId = _tenderId;
        escrow.winner = _winner;
        escrow.totalAmount = _totalAmount;
        escrow.amountReleased = 0;
        escrow.isActive = true;

        for (uint256 i = 0; i < _milestoneDescriptions.length; i++) {
            escrow.milestones.push(
                Milestone({
                    description: _milestoneDescriptions[i],
                    amount: _milestoneAmounts[i],
                    completed: false,
                    paid: false,
                    completionTime: 0
                })
            );

            totalMilestoneAmount += _milestoneAmounts[i];
        }

        require(totalMilestoneAmount == _totalAmount, "Milestones must equal total");

        escrowExists[_tenderId] = true;

        require(
            paymentToken.transferFrom(msg.sender, address(this), _totalAmount),
            "Token transfer failed"
        );

        emit FundsLocked(_tenderId, _winner, _totalAmount);
    }

    function completeMilestone(uint256 _tenderId, uint256 _milestoneIndex)
        external
        onlyOwner
    {
        require(escrowExists[_tenderId], "Escrow not found");

        EscrowContract storage escrow = escrows[_tenderId];
        require(escrow.isActive, "Escrow inactive");
        require(_milestoneIndex < escrow.milestones.length, "Invalid milestone");

        Milestone storage milestone = escrow.milestones[_milestoneIndex];
        require(!milestone.completed, "Already completed");

        milestone.completed = true;
        milestone.completionTime = block.timestamp;

        emit MilestoneCompleted(_tenderId, _milestoneIndex);
    }

    function releaseMilestonePayment(uint256 _tenderId, uint256 _milestoneIndex)
        external
        nonReentrant
        onlyOwner
    {
        require(escrowExists[_tenderId], "Escrow not found");

        EscrowContract storage escrow = escrows[_tenderId];
        require(_milestoneIndex < escrow.milestones.length, "Invalid milestone");

        Milestone storage milestone = escrow.milestones[_milestoneIndex];
        require(milestone.completed, "Not completed");
        require(!milestone.paid, "Already paid");

        milestone.paid = true;
        escrow.amountReleased += milestone.amount;

        require(
            paymentToken.transfer(escrow.winner, milestone.amount),
            "Payment failed"
        );

        emit FundsReleased(_tenderId, escrow.winner, milestone.amount);
    }

    function closeEscrow(uint256 _tenderId) external onlyOwner {
        require(escrowExists[_tenderId], "Escrow not found");

        EscrowContract storage escrow = escrows[_tenderId];
        require(
            escrow.amountReleased == escrow.totalAmount,
            "Funds still locked"
        );

        escrow.isActive = false;
        emit EscrowClosed(_tenderId);
    }

    function getEscrow(uint256 _tenderId)
        external
        view
        returns (EscrowContract memory)
    {
        require(escrowExists[_tenderId], "Escrow not found");
        return escrows[_tenderId];
    }
}

